import "./App.css";
import Header from "./MyComponents/Header";
import { Todos } from "./MyComponents/Todos";
// import { TodoItem } from "./MyComponents/TodoItem";
import { Footer } from "./MyComponents/Footer";
import { AddTodo } from "./MyComponents/AddTodo";
import React, { useState } from 'react';

function App() {
  const onDelete = (todo) => {
    console.log("GG Boi its deleted",todo);

    setTodos(todos.filter(t => t.id !== todo.id));
  }

  const [todos, setTodos] = useState([
    { id: 1, title: "Learn React", desc: "Learn React from CodeWithHarry" },

    { id: 2, title: "Learn Vue", desc: "Learn Vue from CodeWithHarry" },

    { id: 3, title: "Learn Angular", desc: "Learn Angular from CodeWithHarry" },
  ]);

  const addTodo = (title, desc) => {
    console.log("GG Boi its added",title, desc);
    let id = todos[todos.length - 1].id + 1;
    const myTodo = {
      id: id,
      title: title,
      desc: desc
    }
}

  return (
    <>
      <Header title="Todos-List" searchBar={true} />
      <AddTodo addTodo={addTodo}/>
      <Todos todos={todos} onDelete={onDelete} />
      {/* <TodoItem  /> */}
      <Footer />
    </>
  );
}

export default App;
